import React, { useState } from "react";

export default function SearchBar({ onSearch }) {
  const [searchTerm, setSearchTerm] = useState("");
  const [location, setLocation] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    onSearch({ searchTerm, location });
  };

  return (
    <div className="bg-white rounded-2xl shadow-lg p-6 w-full max-w-4xl mx-auto">
      <form onSubmit={handleSubmit} className="space-y-4">
        {/* Barre de recherche principale */}
        <div className="flex gap-2">
          <input
            type="text"
            placeholder="Où cherchez-vous un parking ?"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            className="flex-1 border border-gray-300 rounded-lg px-4 py-3 focus:outline-none focus:ring-2 focus:ring-[#34A853]"
          />
          <button
            type="submit"
            className="bg-[#34A853] text-white px-8 py-3 rounded-lg font-medium hover:bg-[#2d9147] transition whitespace-nowrap"
          >
            Rechercher
          </button>
        </div>

        {/* Options supplémentaires */}
        <div className="flex flex-wrap gap-4 text-sm">
          <label className="flex items-center gap-2">
            <input type="checkbox" className="rounded" />
            <span>Parking couvert</span>
          </label>
          <label className="flex items-center gap-2">
            <input type="checkbox" className="rounded" />
            <span>Accès handicapé</span>
          </label>
          <label className="flex items-center gap-2">
            <input type="checkbox" className="rounded" />
            <span>Surveillance 24/7</span>
          </label>
        </div>
      </form>
    </div>
  );
}
