import React from "react";
import { Link, useNavigate } from "react-router-dom";

export default function Header() {
  const navigate = useNavigate();
  const token = localStorage.getItem("token");
  const userRole = localStorage.getItem("userRole");

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("userRole");
    navigate("/");
  };

  return (
    <header className="bg-white shadow-sm sticky top-0 z-50">
      <div className="container mx-auto px-4 md:px-6 lg:px-12 py-4 flex justify-between items-center">
        {/* Logo exactement comme Zenpark */}
        <Link to="/" className="flex items-center gap-2">
          <div className="w-10 h-10 bg-[#34A853] rounded-full flex items-center justify-center">
            <span className="text-white font-bold text-xl">P</span>
          </div>
          <span className="text-2xl font-bold text-gray-900 lowercase tracking-tight">
            ParkingPartagé
          </span>
        </Link>

        {/* Navigation */}
        <nav className="flex items-center gap-6">
          {token ? (
            <>
              <Link
                to={userRole === "owner" ? "/owner" : "/dashboard"}
                className="text-gray-700 hover:text-[#34A853] transition font-medium"
              >
                Mon compte
              </Link>
              <button
                onClick={handleLogout}
                className="text-gray-700 hover:text-[#34A853] transition font-medium"
              >
                Se déconnecter
              </button>
            </>
          ) : (
            <>
              <Link
                to="/login"
                className="text-gray-700 hover:text-[#34A853] transition font-medium"
              >
                Se connecter
              </Link>
            </>
          )}
          <Link
            to="/register"
            className="bg-[#34A853] text-white px-6 py-2.5 rounded-lg font-semibold hover:bg-[#2d9147] transition shadow-md"
          >
            Trouver une place
          </Link>
        </nav>
      </div>
    </header>
  );
}
