import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Header from "../components/Header";
import Footer from "../components/Footer";
import ProtectedRoute from "../components/ProtectedRoute";

export default function OwnerDashboard() {
  const navigate = useNavigate();
  const [parkings, setParkings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [showAddForm, setShowAddForm] = useState(false);
  const [showEditForm, setShowEditForm] = useState(null);

  // Formulaire pour ajouter un parking
  const [newParking, setNewParking] = useState({
    nom: "",
    adresse: "",
    nombre_places: "",
    tarif_horaire: "",
    tarif_journalier: "",
    tarif_mensuel: "",
    horaire_ouverture: "",
    horaire_fermeture: "",
  });

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
      return;
    }

    // Récupérer les parkings du propriétaire
    fetch("http://localhost:8001/api/parkings", {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then((res) => {
        if (res.status === 401) {
          localStorage.removeItem("token");
          navigate("/login");
          return null;
        }
        return res.json();
      })
      .then((data) => {
        if (data) {
          setParkings(Array.isArray(data) ? data : data.parkings || []);
        }
        setLoading(false);
      })
      .catch((err) => {
        console.error("Erreur:", err);
        setError("Erreur lors du chargement des parkings");
        setLoading(false);
      });
  }, [navigate]);

  const handleAddParking = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");

    try {
      const response = await fetch("http://localhost:8001/api/parkings", {
        method: "POST",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newParking),
      });

      const data = await response.json();

      if (response.ok) {
        setParkings([...parkings, data.parking || data]);
        setNewParking({
          nom: "",
          adresse: "",
          nombre_places: "",
          tarif_horaire: "",
          tarif_journalier: "",
          tarif_mensuel: "",
          horaire_ouverture: "",
          horaire_fermeture: "",
        });
        setShowAddForm(false);
      } else {
        setError(data.message || "Erreur lors de l'ajout du parking");
      }
    } catch (err) {
      setError("Erreur de communication avec le serveur");
    }
  };

  const handleUpdateParking = async (parkingId, updatedData) => {
    const token = localStorage.getItem("token");

    try {
      const response = await fetch(
        `http://localhost:8001/api/parkings/${parkingId}`,
        {
          method: "PUT",
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
          body: JSON.stringify(updatedData),
        }
      );

      const data = await response.json();

      if (response.ok) {
        setParkings(
          parkings.map((p) => (p.id === parkingId ? data.parking || data : p))
        );
        setShowEditForm(null);
      } else {
        setError(data.message || "Erreur lors de la modification");
      }
    } catch (err) {
      setError("Erreur de communication avec le serveur");
    }
  };

  const handleViewRevenues = (parkingId) => {
    const token = localStorage.getItem("token");
    fetch(`http://localhost:8001/api/parkings/${parkingId}/revenues`, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then((res) => res.json())
      .then((data) => {
        alert(`Revenus: ${data.revenus || data.total || "N/A"} €`);
      })
      .catch((err) => {
        alert("Erreur lors de la récupération des revenus");
      });
  };

  return (
    <ProtectedRoute>
      <div className="min-h-screen flex flex-col">
        <Header />
        <main className="flex-1 container mx-auto px-4 py-8">
          <div className="flex justify-between items-center mb-8">
            <h1 className="text-3xl font-bold text-gray-800">
              Espace propriétaire
            </h1>
            <button
              onClick={() => setShowAddForm(true)}
              className="bg-[#34A853] text-white px-6 py-3 rounded-lg hover:bg-[#2d8f45] transition font-medium"
            >
              + Ajouter un parking
            </button>
          </div>

          {error && (
            <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-lg mb-6">
              {error}
            </div>
          )}

          {/* Formulaire d'ajout */}
          {showAddForm && (
            <div className="bg-white rounded-xl shadow-lg p-6 mb-8">
              <h2 className="text-2xl font-bold mb-4">Ajouter un parking</h2>
              <form onSubmit={handleAddParking} className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <input
                    type="text"
                    placeholder="Nom du parking"
                    value={newParking.nom}
                    onChange={(e) =>
                      setNewParking({ ...newParking, nom: e.target.value })
                    }
                    className="px-4 py-2 border rounded-lg"
                    required
                  />
                  <input
                    type="text"
                    placeholder="Adresse"
                    value={newParking.adresse}
                    onChange={(e) =>
                      setNewParking({ ...newParking, adresse: e.target.value })
                    }
                    className="px-4 py-2 border rounded-lg"
                    required
                  />
                  <input
                    type="number"
                    placeholder="Nombre de places"
                    value={newParking.nombre_places}
                    onChange={(e) =>
                      setNewParking({
                        ...newParking,
                        nombre_places: e.target.value,
                      })
                    }
                    className="px-4 py-2 border rounded-lg"
                    required
                  />
                  <input
                    type="number"
                    step="0.01"
                    placeholder="Tarif horaire (€)"
                    value={newParking.tarif_horaire}
                    onChange={(e) =>
                      setNewParking({
                        ...newParking,
                        tarif_horaire: e.target.value,
                      })
                    }
                    className="px-4 py-2 border rounded-lg"
                    required
                  />
                  <input
                    type="number"
                    step="0.01"
                    placeholder="Tarif journalier (€)"
                    value={newParking.tarif_journalier}
                    onChange={(e) =>
                      setNewParking({
                        ...newParking,
                        tarif_journalier: e.target.value,
                      })
                    }
                    className="px-4 py-2 border rounded-lg"
                    required
                  />
                  <input
                    type="number"
                    step="0.01"
                    placeholder="Tarif mensuel (€)"
                    value={newParking.tarif_mensuel}
                    onChange={(e) =>
                      setNewParking({
                        ...newParking,
                        tarif_mensuel: e.target.value,
                      })
                    }
                    className="px-4 py-2 border rounded-lg"
                    required
                  />
                  <input
                    type="time"
                    placeholder="Heure d'ouverture"
                    value={newParking.horaire_ouverture}
                    onChange={(e) =>
                      setNewParking({
                        ...newParking,
                        horaire_ouverture: e.target.value,
                      })
                    }
                    className="px-4 py-2 border rounded-lg"
                    required
                  />
                  <input
                    type="time"
                    placeholder="Heure de fermeture"
                    value={newParking.horaire_fermeture}
                    onChange={(e) =>
                      setNewParking({
                        ...newParking,
                        horaire_fermeture: e.target.value,
                      })
                    }
                    className="px-4 py-2 border rounded-lg"
                    required
                  />
                </div>
                <div className="flex gap-2">
                  <button
                    type="submit"
                    className="bg-[#34A853] text-white px-6 py-2 rounded-lg hover:bg-[#2d8f45] transition"
                  >
                    Ajouter
                  </button>
                  <button
                    type="button"
                    onClick={() => setShowAddForm(false)}
                    className="bg-gray-300 text-gray-700 px-6 py-2 rounded-lg hover:bg-gray-400 transition"
                  >
                    Annuler
                  </button>
                </div>
              </form>
            </div>
          )}

          {/* Liste des parkings */}
          {loading ? (
            <p className="text-gray-500">Chargement...</p>
          ) : parkings.length === 0 ? (
            <div className="bg-gray-50 rounded-lg p-8 text-center">
              <p className="text-gray-600">
                Vous n'avez pas encore de parking. Ajoutez-en un !
              </p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {parkings.map((parking) => (
                <div
                  key={parking.id}
                  className="bg-white rounded-xl shadow-md p-6"
                >
                  <h3 className="text-xl font-bold mb-2">{parking.nom}</h3>
                  <p className="text-gray-600 mb-4">{parking.adresse}</p>
                  <div className="space-y-2 mb-4">
                    <p className="text-sm">
                      <span className="font-semibold">Places:</span>{" "}
                      {parking.nombre_places}
                    </p>
                    <p className="text-sm">
                      <span className="font-semibold">Tarif horaire:</span>{" "}
                      {parking.tarif_horaire} €
                    </p>
                    <p className="text-sm">
                      <span className="font-semibold">Tarif journalier:</span>{" "}
                      {parking.tarif_journalier} €
                    </p>
                    <p className="text-sm">
                      <span className="font-semibold">Tarif mensuel:</span>{" "}
                      {parking.tarif_mensuel} €
                    </p>
                  </div>
                  <div className="flex flex-col gap-2">
                    <button
                      onClick={() =>
                        setShowEditForm(
                          showEditForm === parking.id ? null : parking.id
                        )
                      }
                      className="bg-blue-500 text-white py-2 rounded-lg hover:bg-blue-600 transition"
                    >
                      Modifier
                    </button>
                    <button
                      onClick={() => handleViewRevenues(parking.id)}
                      className="bg-green-600 text-white py-2 rounded-lg hover:bg-green-700 transition"
                    >
                      Consulter les revenus
                    </button>
                  </div>

                  {/* Formulaire d'édition */}
                  {showEditForm === parking.id && (
                    <div className="mt-4 pt-4 border-t">
                      <form
                        onSubmit={(e) => {
                          e.preventDefault();
                          const formData = new FormData(e.target);
                          const updatedData = {
                            tarif_horaire: formData.get("tarif_horaire"),
                            tarif_journalier: formData.get("tarif_journalier"),
                            tarif_mensuel: formData.get("tarif_mensuel"),
                            horaire_ouverture: formData.get("horaire_ouverture"),
                            horaire_fermeture: formData.get("horaire_fermeture"),
                          };
                          handleUpdateParking(parking.id, updatedData);
                        }}
                        className="space-y-2"
                      >
                        <input
                          type="number"
                          step="0.01"
                          name="tarif_horaire"
                          defaultValue={parking.tarif_horaire}
                          placeholder="Tarif horaire"
                          className="w-full px-3 py-1 border rounded"
                        />
                        <input
                          type="number"
                          step="0.01"
                          name="tarif_journalier"
                          defaultValue={parking.tarif_journalier}
                          placeholder="Tarif journalier"
                          className="w-full px-3 py-1 border rounded"
                        />
                        <input
                          type="number"
                          step="0.01"
                          name="tarif_mensuel"
                          defaultValue={parking.tarif_mensuel}
                          placeholder="Tarif mensuel"
                          className="w-full px-3 py-1 border rounded"
                        />
                        <input
                          type="time"
                          name="horaire_ouverture"
                          defaultValue={parking.horaire_ouverture}
                          className="w-full px-3 py-1 border rounded"
                        />
                        <input
                          type="time"
                          name="horaire_fermeture"
                          defaultValue={parking.horaire_fermeture}
                          className="w-full px-3 py-1 border rounded"
                        />
                        <div className="flex gap-2">
                          <button
                            type="submit"
                            className="flex-1 bg-[#34A853] text-white py-1 rounded hover:bg-[#2d8f45]"
                          >
                            Enregistrer
                          </button>
                          <button
                            type="button"
                            onClick={() => setShowEditForm(null)}
                            className="flex-1 bg-gray-300 text-gray-700 py-1 rounded hover:bg-gray-400"
                          >
                            Annuler
                          </button>
                        </div>
                      </form>
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}
        </main>
        <Footer />
      </div>
    </ProtectedRoute>
  );
}
