import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Header from "../components/Header";
import Footer from "../components/Footer";

export default function OwnerDashboard() {
  const navigate = useNavigate();
  const [parkings, setParkings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [showAddForm, setShowAddForm] = useState(false);
  const [showEditForm, setShowEditForm] = useState(null);

  const [newParking, setNewParking] = useState({
    nom: "",
    adresse: "",
    nombre_places: "",
    tarif_horaire: "",
    tarif_journalier: "",
    tarif_mensuel: "",
    horaire_ouverture: "",
    horaire_fermeture: "",
  });

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
      return;
    }

    fetch("http://localhost:8001/api/parkings", {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then((res) => {
        if (res.status === 401) {
          localStorage.removeItem("token");
          navigate("/login");
          return null;
        }
        return res.json();
      })
      .then((data) => {
        if (data) {
          setParkings(Array.isArray(data) ? data : data.parkings || []);
        }
        setLoading(false);
      })
      .catch((err) => {
        setError("Erreur lors du chargement");
        setLoading(false);
      });
  }, [navigate]);

  const handleAddParking = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");

    try {
      const response = await fetch("http://localhost:8001/api/parkings", {
        method: "POST",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newParking),
      });

      const data = await response.json();

      if (response.ok) {
        setParkings([...parkings, data.parking || data]);
        setNewParking({
          nom: "",
          adresse: "",
          nombre_places: "",
          tarif_horaire: "",
          tarif_journalier: "",
          tarif_mensuel: "",
          horaire_ouverture: "",
          horaire_fermeture: "",
        });
        setShowAddForm(false);
      } else {
        setError(data.message || "Erreur");
      }
    } catch (err) {
      setError("Erreur de communication");
    }
  };

  const handleUpdateParking = async (parkingId, updatedData) => {
    const token = localStorage.getItem("token");

    try {
      const response = await fetch(
        `http://localhost:8001/api/parkings/${parkingId}`,
        {
          method: "PUT",
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
          body: JSON.stringify(updatedData),
        }
      );

      const data = await response.json();

      if (response.ok) {
        setParkings(
          parkings.map((p) => (p.id === parkingId ? data.parking || data : p))
        );
        setShowEditForm(null);
      } else {
        setError(data.message || "Erreur");
      }
    } catch (err) {
      setError("Erreur");
    }
  };

  const handleViewRevenues = (parkingId) => {
    const token = localStorage.getItem("token");
    fetch(`http://localhost:8001/api/parkings/${parkingId}/revenues`, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then((res) => res.json())
      .then((data) => {
        alert(`Revenus: ${data.revenus || data.total || "N/A"} €`);
      })
      .catch((err) => {
        alert("Erreur");
      });
  };

  return (
    <div className="min-h-screen flex flex-col">
      <Header />
      <main className="flex-1 container mx-auto px-4 py-8">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-2xl font-bold">Espace propriétaire</h1>
          <button
            onClick={() => setShowAddForm(true)}
            className="bg-[#34A853] text-white px-4 py-2 rounded"
          >
            + Ajouter un parking
          </button>
        </div>

        {error && <p className="text-red-600 mb-4">{error}</p>}

        {showAddForm && (
          <div className="border p-4 mb-6 rounded">
            <h2 className="text-xl font-bold mb-4">Ajouter un parking</h2>
            <form onSubmit={handleAddParking} className="space-y-3">
              <input
                type="text"
                placeholder="Nom"
                value={newParking.nom}
                onChange={(e) =>
                  setNewParking({ ...newParking, nom: e.target.value })
                }
                className="w-full px-3 py-2 border rounded"
                required
              />
              <input
                type="text"
                placeholder="Adresse"
                value={newParking.adresse}
                onChange={(e) =>
                  setNewParking({ ...newParking, adresse: e.target.value })
                }
                className="w-full px-3 py-2 border rounded"
                required
              />
              <input
                type="number"
                placeholder="Nombre de places"
                value={newParking.nombre_places}
                onChange={(e) =>
                  setNewParking({
                    ...newParking,
                    nombre_places: e.target.value,
                  })
                }
                className="w-full px-3 py-2 border rounded"
                required
              />
              <input
                type="number"
                step="0.01"
                placeholder="Tarif horaire (€)"
                value={newParking.tarif_horaire}
                onChange={(e) =>
                  setNewParking({
                    ...newParking,
                    tarif_horaire: e.target.value,
                  })
                }
                className="w-full px-3 py-2 border rounded"
                required
              />
              <input
                type="number"
                step="0.01"
                placeholder="Tarif journalier (€)"
                value={newParking.tarif_journalier}
                onChange={(e) =>
                  setNewParking({
                    ...newParking,
                    tarif_journalier: e.target.value,
                  })
                }
                className="w-full px-3 py-2 border rounded"
                required
              />
              <input
                type="number"
                step="0.01"
                placeholder="Tarif mensuel (€)"
                value={newParking.tarif_mensuel}
                onChange={(e) =>
                  setNewParking({
                    ...newParking,
                    tarif_mensuel: e.target.value,
                  })
                }
                className="w-full px-3 py-2 border rounded"
                required
              />
              <input
                type="time"
                value={newParking.horaire_ouverture}
                onChange={(e) =>
                  setNewParking({
                    ...newParking,
                    horaire_ouverture: e.target.value,
                  })
                }
                className="w-full px-3 py-2 border rounded"
                required
              />
              <input
                type="time"
                value={newParking.horaire_fermeture}
                onChange={(e) =>
                  setNewParking({
                    ...newParking,
                    horaire_fermeture: e.target.value,
                  })
                }
                className="w-full px-3 py-2 border rounded"
                required
              />
              <div className="flex gap-2">
                <button type="submit" className="bg-[#34A853] text-white px-4 py-2 rounded">
                  Ajouter
                </button>
                <button
                  type="button"
                  onClick={() => setShowAddForm(false)}
                  className="bg-gray-400 text-white px-4 py-2 rounded"
                >
                  Annuler
                </button>
              </div>
            </form>
          </div>
        )}

        {loading ? (
          <p>Chargement...</p>
        ) : parkings.length === 0 ? (
          <p>Aucun parking</p>
        ) : (
          <div className="space-y-4">
            {parkings.map((parking) => (
              <div key={parking.id} className="border p-4 rounded">
                <h3 className="text-xl font-bold mb-2">{parking.nom}</h3>
                <p className="text-sm text-gray-600 mb-2">{parking.adresse}</p>
                <p className="text-sm">Places: {parking.nombre_places}</p>
                <p className="text-sm">Horaire: {parking.tarif_horaire} €</p>
                <p className="text-sm">Journalier: {parking.tarif_journalier} €</p>
                <p className="text-sm">Mensuel: {parking.tarif_mensuel} €</p>

                <div className="mt-3 space-x-2">
                  <button
                    onClick={() =>
                      setShowEditForm(
                        showEditForm === parking.id ? null : parking.id
                      )
                    }
                    className="bg-blue-500 text-white px-3 py-1 rounded text-sm"
                  >
                    Modifier
                  </button>
                  <button
                    onClick={() => handleViewRevenues(parking.id)}
                    className="bg-green-600 text-white px-3 py-1 rounded text-sm"
                  >
                    Revenus
                  </button>
                </div>

                {showEditForm === parking.id && (
                  <div className="mt-4 pt-4 border-t">
                    <form
                      onSubmit={(e) => {
                        e.preventDefault();
                        const formData = new FormData(e.target);
                        const updatedData = {
                          tarif_horaire: formData.get("tarif_horaire"),
                          tarif_journalier: formData.get("tarif_journalier"),
                          tarif_mensuel: formData.get("tarif_mensuel"),
                          horaire_ouverture: formData.get("horaire_ouverture"),
                          horaire_fermeture: formData.get("horaire_fermeture"),
                        };
                        handleUpdateParking(parking.id, updatedData);
                      }}
                      className="space-y-2"
                    >
                      <input
                        type="number"
                        step="0.01"
                        name="tarif_horaire"
                        defaultValue={parking.tarif_horaire}
                        placeholder="Tarif horaire"
                        className="w-full px-2 py-1 border rounded text-sm"
                      />
                      <input
                        type="number"
                        step="0.01"
                        name="tarif_journalier"
                        defaultValue={parking.tarif_journalier}
                        placeholder="Tarif journalier"
                        className="w-full px-2 py-1 border rounded text-sm"
                      />
                      <input
                        type="number"
                        step="0.01"
                        name="tarif_mensuel"
                        defaultValue={parking.tarif_mensuel}
                        placeholder="Tarif mensuel"
                        className="w-full px-2 py-1 border rounded text-sm"
                      />
                      <input
                        type="time"
                        name="horaire_ouverture"
                        defaultValue={parking.horaire_ouverture}
                        className="w-full px-2 py-1 border rounded text-sm"
                      />
                      <input
                        type="time"
                        name="horaire_fermeture"
                        defaultValue={parking.horaire_fermeture}
                        className="w-full px-2 py-1 border rounded text-sm"
                      />
                      <div className="flex gap-2">
                        <button
                          type="submit"
                          className="bg-[#34A853] text-white px-3 py-1 rounded text-sm"
                        >
                          Enregistrer
                        </button>
                        <button
                          type="button"
                          onClick={() => setShowEditForm(null)}
                          className="bg-gray-400 text-white px-3 py-1 rounded text-sm"
                        >
                          Annuler
                        </button>
                      </div>
                    </form>
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
      </main>
      <Footer />
    </div>
  );
}
