          adresse: "",
          places_total: "",
          tarif_horaire: "",
          tarif_journalier: "",
          horaire_ouverture: "",
          horaire_fermeture: "",
        });
        fetchParkings();
        alert("‚úÖ Parking ajout√© avec succ√®s !");
      } else {
        setError(data.message || "Erreur lors de l'ajout du parking");
      }
    } catch (err) {
      setError("Erreur de connexion au serveur");
    }
  };

  const handleUpdateParking = async (parkingId, updatedData) => {
    try {
      const response = await fetch(
        `http://localhost:8001/api/parkings/${parkingId}`,
        {
          method: "PUT",
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
          body: JSON.stringify(updatedData),
        }
      );

      if (response.ok) {
        fetchParkings();
        alert("‚úÖ Parking mis √† jour avec succ√®s !");
      } else {
        alert("‚ùå Erreur lors de la mise √† jour");
      }
    } catch (err) {
      alert("‚ùå Erreur de connexion");
    }
