                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium mb-2">
                    Tarif journalier (‚Ç¨)
                  </label>
                  <input
                    type="number"
                    step="0.01"
                    value={formData.tarif_journalier}
                    onChange={(e) =>
                      setFormData({
                        ...formData,
                        tarif_journalier: e.target.value,
                      })
                    }
                    className="w-full border rounded-lg px-4 py-2"
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium mb-2">
                    Heure d'ouverture
                  </label>
                  <input
                    type="time"
                    value={formData.horaire_ouverture}
                    onChange={(e) =>
                      setFormData({
                        ...formData,
                        horaire_ouverture: e.target.value,
                      })
                    }
                    className="w-full border rounded-lg px-4 py-2"
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium mb-2">
                    Heure de fermeture
                  </label>
                  <input
                    type="time"
                    value={formData.horaire_fermeture}
                    onChange={(e) =>
                      setFormData({
                        ...formData,
                        horaire_fermeture: e.target.value,
                      })
