import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Header from "../components/Header";
import Footer from "../components/Footer";

export default function Dashboard() {
  const navigate = useNavigate();
  const token = localStorage.getItem("token");
  const [reservations, setReservations] = useState([]);
  const [abonnements, setAbonnements] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    if (!token) {
      navigate("/login");
      return;
    }

    fetchData();
  }, [token, navigate]);

  const fetchData = async () => {
    try {
      setLoading(true);

      // R√©cup√©rer les r√©servations
      const resReservations = await fetch(
        "http://localhost:8001/api/reservations",
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      // R√©cup√©rer les abonnements
      const resAbonnements = await fetch(
        "http://localhost:8001/api/abonnements",
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (resReservations.ok) {
        const data = await resReservations.json();
        setReservations(Array.isArray(data) ? data : []);
      }

      if (resAbonnements.ok) {
        const data = await resAbonnements.json();
        setAbonnements(Array.isArray(data) ? data : []);
      }
    } catch (err) {
      setError("Erreur lors du chargement des donn√©es");
    } finally {
      setLoading(false);
    }
  };

  const handleEnterParking = async (reservationId) => {
    try {
      const response = await fetch(
        `http://localhost:8001/api/reservations/${reservationId}/enter`,
        {
          method: "POST",
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        }
      );

      if (response.ok) {
        alert("‚úÖ Entr√©e enregistr√©e !");
        fetchData();
      } else {
        alert("‚ùå Erreur lors de l'entr√©e");
      }
    } catch (err) {
      alert("‚ùå Erreur de connexion");
    }
  };

  const handleExitParking = async (reservationId) => {
    try {
      const response = await fetch(
        `http://localhost:8001/api/reservations/${reservationId}/exit`,
        {
          method: "POST",
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        }
      );

      if (response.ok) {
        alert("‚úÖ Sortie enregistr√©e !");
        fetchData();
      } else {
        alert("‚ùå Erreur lors de la sortie");
      }
    } catch (err) {
      alert("‚ùå Erreur de connexion");
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex flex-col">
        <Header />
        <main className="flex-grow flex items-center justify-center">
          <p className="text-gray-600">Chargement...</p>
        </main>
        <Footer />
      </div>
    );
  }

  return (
    <div className="min-h-screen flex flex-col">
      <Header />
      <main className="flex-grow container mx-auto px-4 py-8">
        <h1 className="text-3xl font-bold mb-8 text-[#34A853]">
          Mon Tableau de Bord
        </h1>

        {error && (
          <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-lg mb-6">
            {error}
          </div>
        )}

        {/* R√©servations */}
        <section className="mb-12">
          <h2 className="text-2xl font-semibold mb-4">Mes R√©servations</h2>
          {reservations.length === 0 ? (
            <p className="text-gray-600">Aucune r√©servation pour le moment.</p>
          ) : (
            <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
              {reservations.map((reservation) => (
                <div
                  key={reservation.id}
                  className="bg-white rounded-lg shadow-md p-6"
                >
                  <h3 className="font-semibold text-lg mb-2">
                    Parking #{reservation.parking_id}
                  </h3>
                  <p className="text-gray-600 mb-2">
                    üìÖ {new Date(reservation.date_debut).toLocaleDateString()}
                  </p>
                  <p className="text-gray-600 mb-4">
                    ‚è∞ {reservation.heure_debut} - {reservation.heure_fin}
                  </p>
                  <div className="flex gap-2">
                    <button
                      onClick={() => handleEnterParking(reservation.id)}
                      className="flex-1 bg-[#34A853] text-white py-2 rounded-lg hover:bg-[#2d9147] transition"
                    >
                      Entrer
                    </button>
                    <button
                      onClick={() => handleExitParking(reservation.id)}
                      className="flex-1 bg-gray-600 text-white py-2 rounded-lg hover:bg-gray-700 transition"
                    >
                      Sortir
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </section>

        {/* Abonnements */}
        <section>
          <h2 className="text-2xl font-semibold mb-4">Mes Abonnements</h2>
          {abonnements.length === 0 ? (
            <p className="text-gray-600">Aucun abonnement pour le moment.</p>
          ) : (
            <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
              {abonnements.map((abonnement) => (
                <div
                  key={abonnement.id}
                  className="bg-white rounded-lg shadow-md p-6"
                >
                  <h3 className="font-semibold text-lg mb-2">
                    Abonnement #{abonnement.id}
                  </h3>
                  <p className="text-gray-600 mb-2">
                    Parking #{abonnement.parking_id}
                  </p>
                  <p className="text-gray-600 mb-2">
                    üìÖ Du {new Date(abonnement.date_debut).toLocaleDateString()}{" "}
                    au {new Date(abonnement.date_fin).toLocaleDateString()}
                  </p>
                  <p className="text-[#34A853] font-medium">
                    {abonnement.tarif}‚Ç¨/mois
                  </p>
                </div>
              ))}
            </div>
          )}
        </section>
      </main>
      <Footer />
    </div>
  );
}
