import React from "react";
import { Link, useNavigate } from "react-router-dom";

export default function Header() {
  const navigate = useNavigate();
  const token = localStorage.getItem("token");
  const userRole = localStorage.getItem("userRole");

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("userRole");
    navigate("/");
  };

  return (
    <header className="bg-white shadow-md">
      <div className="container mx-auto px-4 py-4 flex justify-between items-center">
        {/* Logo */}
        <Link to="/" className="text-2xl font-bold text-[#34A853]">
          ParkingPartagé
        </Link>

        {/* Navigation */}
        <nav className="flex items-center gap-6">
          <Link
            to="/"
            className="text-gray-700 hover:text-[#34A853] transition"
          >
            Accueil
          </Link>

          {token ? (
            <>
              <Link
                to={userRole === "owner" ? "/owner" : "/dashboard"}
                className="text-gray-700 hover:text-[#34A853] transition"
              >
                Mon compte
              </Link>
              <button
                onClick={handleLogout}
                className="text-gray-700 hover:text-[#34A853] transition"
              >
                Se déconnecter
              </button>
            </>
          ) : (
            <>
              <Link
                to="/login"
                className="text-gray-700 hover:text-[#34A853] transition"
              >
                Se connecter
              </Link>
              <Link
                to="/register"
                className="bg-[#34A853] text-white px-4 py-2 rounded-lg hover:bg-[#2d9147] transition"
              >
                S'inscrire
              </Link>
            </>
          )}
        </nav>
      </div>
    </header>
  );
}
